-- find records that exist in most of the affected tables
SELECT
    i.inspevnt_gd,
    i.brkey,
    i.inspkey,
    doc.doc_key,
    ass.inspevnt_gd        AS asmt_gd,
    elem.structure_unit_gd AS elem_id,
    stat.inspevnt_gd       AS stat_gd,
    cand.inspevnt_gd       AS cand_gd
FROM
    inspevnt i
    INNER JOIN kdotblp_documents   doc ON i.inspevnt_gd = doc.inspevnt_gd
    LEFT OUTER JOIN pon_assessment      ass ON i.inspevnt_gd = ass.inspevnt_gd
    INNER JOIN pon_elem_insp       elem ON i.inspevnt_gd = elem.inspevnt_gd
    LEFT OUTER JOIN pon_insp_status_log stat ON i.inspevnt_gd = stat.inspevnt_gd
    LEFT OUTER JOIN pon_insp_workcand   cand ON i.inspevnt_gd = cand.inspevnt_gd
    INNER JOIN kdotblp_inspections kins ON i.inspevnt_gd = kins.inspevnt_gd
WHERE
    i.inspevnt_gd NOT IN (
        SELECT
            inspevnt_gd
        FROM
            kdotblp_load_ratings
    )
ORDER BY
    1,
    4,
    6;

-- find inspections with more than one document and at least one element record
SELECT
    elem.brkey,
    elem.inspkey,
    elem.inspevnt_gd
FROM
    pon_elem_insp elem
WHERE
    elem.inspevnt_gd IN (
        -- find inspections with more than one document, but not too many
        SELECT
            doc.inspevnt_gd
        FROM
            kdotblp_documents doc
        GROUP BY
            doc.inspevnt_gd
        HAVING COUNT(*) > 1
               AND COUNT(*) < 9
    )
ORDER BY
    1,
    2,
    3;

